"use client";

import React, { useState } from "react";
import { ThreeDCardDemo } from "./3dcardeffect"; // adjust the path if needed

const cardsData = [
  {
    title: "Make things float in air",
    description: "Hover over this card to unleash the power of CSS perspective",
    imageUrl:
      "https://images.unsplash.com/photo-1441974231531-c6227db76b6e?q=80&w=2560&auto=format&fit=crop&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D",
    tryLink: "https://twitter.com/mannupaaji",
    signUpAction: () => alert("Sign Up clicked!"),
  },
  {
    title: "Another amazing card",
    description: "This is the second card description",
    imageUrl:
      "https://images.unsplash.com/photo-1506744038136-46273834b3fb?q=80&w=2560&auto=format&fit=crop",
    tryLink: "https://example.com",
    signUpAction: () => alert("Second card Sign Up clicked!"),
  },
  // Add more cards here if you want
];

export function ThreeDCardCarousel() {
  const [currentIndex, setCurrentIndex] = useState(0);

  const prev = () => {
    setCurrentIndex((i) => (i === 0 ? cardsData.length - 1 : i - 1));
  };

  const next = () => {
    setCurrentIndex((i) => (i === cardsData.length - 1 ? 0 : i + 1));
  };

  return (
    <div className="flex flex-col items-center space-y-6">
      <ThreeDCardDemo {...cardsData[currentIndex]} />
      <div className="flex space-x-4">
        <button
          onClick={prev}
          className="px-4 py-2 rounded bg-gray-300 dark:bg-gray-700"
        >
          Prev
        </button>
        <button
          onClick={next}
          className="px-4 py-2 rounded bg-gray-300 dark:bg-gray-700"
        >
          Next
        </button>
      </div>
    </div>
  );
}
